import React, { useState } from "react";
import { updateCampaign, deleteCampaign } from "../services/api";

export default function CampaignList({ campaigns, onRefetch }) {
  // âœ… State to manage which campaign is being edited
  const [editingCampaign, setEditingCampaign] = useState(null);
  const [updatedCampaign, setUpdatedCampaign] = useState({
    "Campaign Name": "",
    "Client Name": "",
    "Start Date": "",
    Status: "",
  });

  // âœ… Function to set campaign status (Active, Paused, Completed)
  const setStatus = async (name, status) => {
    await updateCampaign(name, { Status: status });
    onRefetch && onRefetch();
  };

  // âœ… Function to delete campaign
  const remove = async (name) => {
    if (!window.confirm("Delete campaign?")) return;
    await deleteCampaign(name);
    onRefetch && onRefetch();
  };

  // âœ… Function to start editing
  const handleEdit = (campaign) => {
    setEditingCampaign(campaign);
    setUpdatedCampaign({
      "Campaign Name": campaign["Campaign Name"],
      "Client Name": campaign["Client Name"],
      "Start Date": campaign["Start Date"],
      Status: campaign["Status"],
    });
  };

  // âœ… Handle field changes while editing
  const handleChange = (e) => {
    const { name, value } = e.target;
    setUpdatedCampaign((prev) => ({ ...prev, [name]: value }));
  };

  // âœ… Save updated campaign
  const handleUpdate = async () => {
    if (!editingCampaign) return;

    await updateCampaign(editingCampaign["Campaign Name"], updatedCampaign);
    alert("Campaign updated successfully!");
    setEditingCampaign(null);
    onRefetch && onRefetch();
  };

  return (
    <div className="card">
      <h3>Campaigns</h3>

      <table>
        <thead>
          <tr>
            <th>Name</th>
            <th>Client</th>
            <th>Start</th>
            <th>Status</th>
            <th>Actions</th>
          </tr>
        </thead>
        <tbody>
          {campaigns.map((c) => (
            <tr key={c["Campaign Name"]}>
              <td>{c["Campaign Name"]}</td>
              <td>{c["Client Name"]}</td>
              <td>{c["Start Date"]}</td>
              <td>{c.Status}</td>
              <td className="actions">
                <button onClick={() => setStatus(c["Campaign Name"], "Active")}>
                  Active
                </button>
                <button onClick={() => setStatus(c["Campaign Name"], "Paused")}>
                  Pause
                </button>
                <button
                  onClick={() => setStatus(c["Campaign Name"], "Completed")}
                >
                  Complete
                </button>
                <button onClick={() => handleEdit(c)}>Update</button>
                <button onClick={() => remove(c["Campaign Name"])}>Delete</button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>

      {/* âœ… Edit form appears below the table */}
      {editingCampaign && (
        <div
          style={{
            marginTop: "20px",
            padding: "15px",
            border: "1px solid #ddd",
            borderRadius: "8px",
            backgroundColor: "#f9f9f9",
          }}
        >
          <h3>Update Campaign: {editingCampaign["Campaign Name"]}</h3>

          <label>Campaign Name:</label>
          <input
            type="text"
            name="Campaign Name"
            value={updatedCampaign["Campaign Name"]}
            onChange={handleChange}
          />

          <label>Client Name:</label>
          <input
            type="text"
            name="Client Name"
            value={updatedCampaign["Client Name"]}
            onChange={handleChange}
          />

          <label>Start Date:</label>
          <input
            type="date"
            name="Start Date"
            value={updatedCampaign["Start Date"]}
            onChange={handleChange}
          />

          <label>Status:</label>
          <select
            name="Status"
            value={updatedCampaign["Status"]}
            onChange={handleChange}
          >
            <option value="Active">Active</option>
            <option value="Paused">Paused</option>
            <option value="Completed">Completed</option>
          </select>

          <br />
          <button onClick={handleUpdate}>ðŸ’¾ Save Changes</button>
          <button
            onClick={() => setEditingCampaign(null)}
            style={{ marginLeft: "10px", backgroundColor: "lightgray" }}
          >
            Cancel
          </button>
        </div>
      )}
    </div>
  );
}
