import React from 'react';
import { updateCampaign, deleteCampaign } from '../services/api';

export default function CampaignList({ campaigns, onRefetch }) {
  const setStatus = async (name, status) => {
    await updateCampaign(name, { Status: status });
    onRefetch && onRefetch();
  };
  const remove = async (name) => {
    if (!window.confirm("Delete campaign?")) return;
    await deleteCampaign(name);
    onRefetch && onRefetch();
  };
  return (
    <div className="card">
      <h3>Campaigns</h3>
      <table>
        <thead>
          <tr><th>Name</th><th>Client</th><th>Start</th><th>Status</th><th>Actions</th></tr>
        </thead>
        <tbody>
          {campaigns.map(c => (
            <tr key={c["Campaign Name"]}>
              <td>{c["Campaign Name"]}</td>
              <td>{c["Client Name"]}</td>
              <td>{c["Start Date"]}</td>
              <td>{c.Status}</td>
              <td className="actions">
                <button onClick={()=>setStatus(c["Campaign Name"], "Active")}>Active</button>
                <button onClick={()=>setStatus(c["Campaign Name"], "Paused")}>Pause</button>
                <button onClick={()=>setStatus(c["Campaign Name"], "Completed")}>Complete</button>
                <button onClick={()=>remove(c["Campaign Name"])}>Delete</button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
}
